SELECT
seq
, id
, password
, nickname
, emailAccount
, emailDomain
, gender
, dob
, agreeDT
, delNy
FROM user AS m
WHERE 1=1
AND seq = #{seq}
AND id = #{id}
AND password = #{password}
AND nickname = #{nickname}
AND emailAccount = #{emailAccount}
AND emailDomain = #{emailDomain}
AND gender = #{gender}
AND agreeDT = now()
AND delNy = 0

INSERT INTO user(
id
, password
, nickname
, emailAccount
, emailDomain
, gender
, dob
, agreeDT
, delNy
) values (
#{id}
, #{password}
, #{nickname}
, #{emailAccount}
, #{emailDomain}
, #{gender}
, #{dob}
, now()
, 0
)
